package com.bume.core.security.service;import com.bume.core.security.jwt.Token;import com.bume.core.usuario.TipoAcesso;import com.bume.core.usuario.Usuario;import lombok.Getter;import lombok.Setter;import javax.enterprise.context.RequestScoped;@RequestScopedpublic class Logado {    @Getter    @Setter    private Token token;    public Usuario getUsuario() {        if (token != null)            return token.getUser();        return null;    }    public Boolean isAdminGeral() {        return TipoAcesso.ADMIN_GERAL.equals(getUsuario().getTipoAcesso());    }    public Boolean isAdminEmpresa() {        return TipoAcesso.ADMIN_EMPRESA.equals(getUsuario().getTipoAcesso());    }    public Boolean isRHEmpresa() {        return TipoAcesso.EMPRESA_RH.equals(getUsuario().getTipoAcesso());    }}