package com.bume.core.usuario;import io.quarkus.hibernate.orm.panache.PanacheEntityBase;import lombok.*;import javax.persistence.*;import java.io.Serializable;import java.time.LocalDate;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Builder@Entity@Table(name = "usuario")public class Usuario extends PanacheEntityBase implements Serializable, Cloneable {    @Id    @Column    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false)    private String nome;    @Column(length = 14, unique = true)    private String cpf;    @Column(length = 20)    private String rg;    @Column(name = "data_nascimento", length = 20)    private LocalDate dataNascimento;    @Column(length = 100)    private String email;    @Column(name = "telefone_celular", length = 20)    private String telefoneCelular;    @Column(length = 50, nullable = false, unique = true)    private String login;    @Column(length = 100, nullable = false, unique = true)    private String senha;    @Column(nullable = false)    private Boolean ativo;    @Column(name = "tipo_acesso", nullable = false)    @Enumerated(EnumType.STRING)    private TipoAcesso tipoAcesso;    public static Usuario findByCpfOrEmail(String cpf, String email) {        return find("cpf = ?1 or email = ?2", cpf, email).firstResult();    }    public static Usuario findByLoginAndAtivo(String value) {        return find("login = ?1 and ativo = true", value).firstResult();    }    @Override    protected Object clone() throws CloneNotSupportedException {        return super.clone();    }}